import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ImageBackground, TextInput } from 'react-native';
import { TouchableOpacity } from 'react-native-web';
const image = require('./assets/fundo.png')
export default function App() {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} resizeMode="cover" style={styles.image}>
        <View style={styles.cont_size}>
          <Text style={{ color: 'white' }}>Bem Vindo!</Text>
          <Text style={{ color: 'white' }}>Bem vindo, ao melhor App</Text>
          <View>
            <Text>Usuário</Text>
            <TextInput placeholder="E-mail" />
            <Text>Senha</Text>
            <TextInput placeholder="Senha" />
            <Text>Esqueceu a senha?</Text>
          </View>
          <View>
            <TouchableOpacity>
              <Text>Entrar</Text>
            </TouchableOpacity>
            <Text>Ou continue com</Text>
          </View>
          <Text>Não tem conta?<Text style={{ fontWeight: 'bold' }}>Cadastre-se</Text></Text>
        </View>
      </ImageBackground>



    </View>
  );
}

const styles = StyleSheet.create({
  image: {
    width: '100vw',
    height: '100vh',
    justifyContent: 'center',
  },
  cont_size: {
    color: 'white'
  }
});
