import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ImageBackground, TextInput } from 'react-native';
import { TouchableOpacity } from 'react-native-web';
const image = require('./assets/fundo.png')
export default function App() {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} resizeMode="cover" style={styles.image}>
        <View style={styles.cont_size}>
          <Text style={{ color: 'white' }}>Bem Vindo!</Text>
          <Text style={{ color: '#A4A4A4' }}>Bem vindo, ao melhor App</Text>
          <View>
            <Text style={{ color: '#A4A4A4' }}>Usuário</Text>
            <TextInput style={styles.input} placeholder="E-mail" placeholderTextColor="#A4A4A4" />
            <Text style={{ color: '#A4A4A4' }}>Senha</Text>
            <TextInput style={styles.input} placeholder="Senha" placeholderTextColor="#A4A4A4" />
            <Text style={{ color: '#A4A4A4' }}>Esqueceu a senha?</Text>
          </View>
          <View>
            <TouchableOpacity style={styles.btn}>
              <Text style={{ color: 'white' }}>Entrar</Text>
            </TouchableOpacity>
            <Text style={{ color: '#A4A4A4' }}>Ou continue com</Text>
          </View>
          <Text style={{ color: '#A4A4A4' }}>Não tem conta?<Text style={{ fontWeight: 'bold' }}>Cadastre-se</Text></Text>
        </View>
      </ImageBackground>



    </View>
  );
}

const styles = StyleSheet.create({
  image: {
    width: '100vw',
    height: '100vh',
    justifyContent: 'center',
  },
  cont_size: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  },
  btn: {
    color: 'white',
    backgroundImage: 'linear-gradient(90deg, rgba(156,63,228,1) 0%, rgba(198,86,71,1) 100%)'
  },
  input: {
    padding: '10px',
    borderRadius: '8px',
    backgroundImage: 'linear-gradient(90deg, rgba(164,164,164,0.4) 0%, rgba(164,164,164,0) 100%)'

  }
});
